import { addPropertyControls } from "framer";
import * as React from "react";
import {
  VerticalAirtable,
  Props,
  airtablePropertyControls
} from "./VerticalAirtable";

HorizontalAirtable.defaultProps = {
  height: 375,
  width: 810,
  direction: "horizontal" as "horizontal",
  onItemTapped: () => {}
};

export function HorizontalAirtable(props: Props) {
  return <VerticalAirtable {...props} />;
}

addPropertyControls(HorizontalAirtable, airtablePropertyControls);
