import { Override } from "framer"

export function Hover(): Override {
    return {
        whileHover: {
            scale: 0.8,
            borderRadius: 5,
            rotate: 180,
            background: "green",
        },
        drag: true,
    }
}

export function Expand(): Override {
    return {
        whileHover: {
            height: 100,
        },
    }
}
