import * as React from 'react';
import {Provider as StyletronProvider} from 'styletron-react';
import {LightTheme, LightThemeMove, DarkTheme, DarkThemeMove} from 'baseui/themes';
import BaseProvider from 'baseui/helpers/base-provider';
import {engine} from './StyletronEngine';
import {distType} from './distType';

const themes = {
  light: distType === 'public' ? LightTheme : LightThemeMove,
  dark: distType === 'public' ? DarkTheme : DarkThemeMove,
};

export const ThemeProvider = props => {
  return (
    <StyletronProvider value={engine}>
      <BaseProvider theme={themes[props.theme] || themes.light}>{props.children}</BaseProvider>
    </StyletronProvider>
  );
};
